import React from 'react'
import {Link} from 'react-router-dom'

export default function AllProducts(props) {
    console.log(props)
    let arr = [...props.productArr];
    console.log(arr)
    if (props.match.params.orderBy === "asc") {
        arr.sort((a, b) => a.price - b.price);
      } else if (props.match.params.orderBy === "desc") {
        arr.sort((a, b) => b.price - a.price);
      }
    return (
        <React.Fragment>
        <h2> Products </h2>
        {props.match.params.orderBy === "asc" ? (
          <Link to="/products/desc"> Order By Descending Price</Link>
        ) : (
          <Link to="/products/asc"> Order By Ascending Price</Link>
        )}
        <div style={{ border: "1px solid black" }}>
          {arr.map(ele => {
            return (
              <div key={element.id} >
                  { <Product
                                        img={ele.img}
                                        name={ele.name}
                                        price={ele.price}
                                    />}
                <p> Name : {element.name} </p>
                <p> Price : {element.price} </p>
              </div>
            );
          })}
        </div>
      </React.Fragment>
    )
}
