}

export default function Product(props) {


  return (
    <Card style={{width:"250px"}}>
      <CardActionArea>
        <CardMedia 
           style={{height:"300px", width:"300px"}}
          component="img"
          image={props.img}

        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            {props.name}
          </Typography>
          <Typography gutterBottom variant="h5" component="h2">
            Rs.{props.price}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button style={{width:"300px"}} onClick={handleClick} variant="contained" color="primary">
          ADD TO CART
        </Button>
      </CardActions>
    </Card>

  );
}
