import React, { Component } from 'react'
import Product from "./Product"

export default class Shoppingcart extends Component {
    constructor(props) {
        super(props);
        this.state = {
          totalproducts: [],
          addedtocart: [],
          ready: false
        };
      }
      componentDidMount() {
        this.setState(
          {
            totalproducts: this.props.products
          },
          () => {
            if (this.props.location.search.length > 5) {
              const query = new URLSearchParams(this.props.location.search);
              let queryID = query.get("id");
              let selected = this.state.totalproducts.find(
                ({ id }) => id === queryID
              );
              this.props.handleAdded(selected);

              this.setState({
                ready: true
              });
            }
          }
        );

      }
      componentWillUnmount() {
        this.setState({
          totalproducts: ""
        });
      }

    render() {

            return (
              <div className="text-center mt-5">
                <div className="text-center">
                  <p className="text-muted  h2 mb-5">Shopping cart</p>
                  </div>
                <div className="row">

                  {
                    this.props.added.map(ele => {
                      return (
                        <div className="col-3" key={ele.id}>
                            <Product
                                img={ele.img}
                                name={ele.name}
                                price={ele.price}
                            />      
                    </div>
                      );
                    })
                  }
                  <br></br>
                </div>



              </div>
            );

        }
}
