import React, { Component } from 'react'
import Shopcartdesign from './Shopcartdesign'

var cartttt=JSON.parse(localStorage.getItem('arrrr')); 

export default class Shoppingcart extends Component {

  componentWillUnmount(old){
     old.push
  }
  render() {
   console.log(cartttt)

   var check =JSON.parse(localStorage.getItem('dataofcart1'));
   if (check == null) {
     localStorage.setItem('dataofcart1',JSON.stringify([]));
   }
   else {
     var olddata=JSON.parse(localStorage.getItem('dataofcart1'));
     olddata.push(cartttt)
     localStorage.setItem('dataofcart1',JSON.stringify(olddata));

   }
   var old=JSON.parse(localStorage.getItem('dataofcart1'));
   console.log(old)



    return (
      <div>
         <div style={{ border: "1px solid black" }}>
            <div className="container">
            <h2>THIS IS YOUR  SELECTED CART ITEM </h2>
                <div className="row">
                {old.map(ele => {
            return (
              <div key={ele.id} className="col-2 ml-2 mr-2" >
                  { <Shopcartdesign
                                        img={ele.img}
                                        name={ele.name}
                                        price={ele.price}
                 />}

              </div>
            );
          })}
                </div>

            </div>

        </div>
      </div>
    )
  }
}
