import React, { Component } from 'react'
import Product from "./Product"

export default class Shoppingcart extends Component {
    constructor(props) {
        super(props);
        this.state = {
          addedtocart: [],
          ready: false
        };
      }
      componentDidMount() {

          () => {
            if (this.props.location.search.length > 5) {
              const query = new URLSearchParams(this.props.location.search);
              let queryID = query.get("id");
              let selecteditem = this.state.totalproducts.find(
                ({ id }) => id === queryID
              );
              this.props.handleAdded(selecteditem);

              this.setState({
                ready: true
              });
            }
          }


      }

    render() {

            return (
              <div className="text-center mt-5">
                <div className="text-center">
                  <p className="text-muted  h2 mb-5">Shopping cart</p>
                  </div>
                <div className="row">

                  {this.state.ready && this.props.added.length > 0 ? (
                    this.props.added.map(ele => {
                      return (
                        <div className="col-3" key={ele.id}>
                            <Product
                                img={ele.img}
                                name={ele.name}
                                price={ele.price}
                            />      
                    </div>
                      );
                    })
                  ) : (
                    <div className="text-center col">
                      <p className="display-4 ">Cart is empty</p>
                      <p className="text-warning h5 mb-5">Go shop and add some</p>
                    </div>
                  )}
                  <br></br>
                </div>



              </div>
            );

        }
}
