import React from "react";
import {
  Card,
  CardActionArea,
  CardActions,
  CardContent,
  CardMedia,
  Button,
  Typography
} from "@material-ui/core";
import {Link} from "r"


const handleClick = (props) => {
  // return (<Redirect to={{ pathname: "/shoppingcart" }} />)
  props.history.push('/login')

}

export default function Product(props) {
  // console.log('props : ', props)

  return (

    <Card style={{width:"250px"}}>
      <CardActionArea>
        <CardMedia 
           style={{height:"300px", width:"300px"}}
          component="img"
          image={props.img}

        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            {props.name}
          </Typography>
          <Typography gutterBottom variant="h5" component="h2">
            Rs.{props.price}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        {/* <Button style={{width:"300px"}} onClick={() => handleClick(props)} variant="contained" color="primary">
          ADD TO CART
        </Button> */}
        <Link to={`/shoppingcart/?id=${props.id}`}>
                      <Button  onClick={() => handleClick(props)} size="small" color="primary">
                        Add to cart
                      </Button>
                    </Link>
      </CardActions>
    </Card>

  );
}
