import React from "react";
import {
  Card,
  CardActionArea,
  CardActions,
  CardContent,
  CardMedia,
  Button,
  Typography
} from "@material-ui/core";


const handleClick = (props) => {
  // return (<Redirect to={{ pathname: "/shoppingcart" }} />)
  //console.log(props)
  props.history.push('/login')
  var array = {'id': props.match.params.id, 'img': props.img, 'name': props.name, 'price': props.price}
  console.log("array:" ,array)

  localStorage.setItem('arrrr',JSON.stringify(array));

  var check =JSON.parse(localStorage.getItem('datacart'));
  console.log(check)
  if (check == null) {
    localStorage.setItem('datacart',JSON.stringify([]));
  }
  else {
    var olddata=JSON.parse(localStorage.getItem('datacart1'));
    olddata.push(array)
    localStorage.setItem('datacart1',JSON.stringify(olddata));

  }
  var old=JSON.parse(localStorage.getItem('datacart1'));
  console.log(old)


}

export default function Product(props) {
  // console.log('props : ', props)

  return (

    <Card style={{width:"250px"}}>
      <CardActionArea>
        <CardMedia 
           style={{height:"300px", width:"300px"}}
          component="img"
          image={props.img}

        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            {props.name}
          </Typography>
          <Typography gutterBottom variant="h5" component="h2">
            Rs.{props.price}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button style={{width:"300px"}} onClick={() => handleClick(props)} variant="contained" color="primary">
          ADD TO CART
        </Button>
          {/* <Link to={`/shoppingcart/?id=${props.id}`}>
                      <Button size="small" onClick={() => handleClick(props)} variant="contained" color="primary">
                        Add to cart
                      </Button>
                    </Link> */}

      </CardActions>
    </Card>

  );
}
