import React, { Component } from 'react'
import Product from "./Product"

export default class Shoppingcart extends Component {
    constructor(props) {
        super(props);
        this.state = {
          addtocart: [],
          show: false
        };
      }
      componentDidMount() {() => {
            if (this.props.location.search.length > 5) {
              const query = new URLSearchParams(this.props.location.search);
              let queryID = query.get("id");
              let selecteditem = this.state.totalproducts.find(
                ({ id }) => id === queryID
              );
              this.props.handleAdded(selecteditem);

              this.setState({
                show:true
              });
            }
          }


      }

    render() {

            return (
              <div className="text-center mt-5">
                <div className="text-center text-danger">
                  <p className="h3 mb-5">This is you shopping cart </p>
                  </div>
                <div className="row">

                  {this.state.selecteditem && this.props.added.length > 0 ? (
                    this.props.added.map(ele => {
                      return (
                        <div className="col-4" key={ele.id}>
                            <Product
                                img={ele.img}
                                name={ele.name}
                                price={ele.price}
                            />      
                    </div>
                      );
                    })
                  ) : (
                    <div className="text-center col">
                      <p className="h3 ">Shopping Cart is empty</p>
                      <p className="text-success h4 mb-5">Click add to cart to shop</p>
                    </div>
                  )}
                  <br></br>
                </div>



              </div>
            );

        }
}
