import {
  Card,
  CardActionArea,
  CardActions,
  CardContent,
  CardMedia,
  Button,
  Typography
} from "@material-ui/core";
import Shoppingcart from './Shoppingcart'


export default class Product extends Component {
  constructor(props){
    super(props)
    this.state={
       productid:[]
    }
  }
  handleClick=(id)=>{
    this.setState({
      productid:id
    })
  }
  render() {

    return (
      <div>
        <Card style={{width:"250px"}}>
      <CardActionArea>
        <CardMedia 
           style={{height:"300px", width:"300px"}}
          component="img"
          image={this.props.img}

        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            {this.props.name}
          </Typography>
          <Typography gutterBottom variant="h5" component="h2">
            Rs.{this.props.price}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
  <Button style={{width:"300px"}} onClick={()=> handleCLickthis.props.id} variant="contained" color="primary">
          ADD TO CART
        </Button>
        <Shoppingcart data={this.state.productid}/>


      </CardActions>
    </Card>
      </div>
    )
  }
}
